import Card from "../../ui/Card";
import Modal from "../../ui/Modal";
import classes from "./Body.module.css";

import {useState, useEffect} from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Body(props) {
  const dummy_data = [
    {
      id: "1",
      url: "https://upload.wikimedia.org/wikipedia/commons/c/c0/Moreau_-_Saint_Georges_terrassant_le_dragon%2C_d%27apr%C3%A8s_Carpaccio%2C_Cat._195.jpg",
      title: "St. George and the Dragon",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "2",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/e/eb/The_Frogs_Asking_for_a_King.jpg/800px-The_Frogs_Asking_for_a_King.jpg",
      title: "The Frogs Asking for a King",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "3",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/11/Gustave_Moreau_-_Diom%C3%A8de_d%C3%A9vor%C3%A9_par_ses_chevaux.jpg/800px-Gustave_Moreau_-_Diom%C3%A8de_d%C3%A9vor%C3%A9_par_ses_chevaux.jpg",
      title: "Diomedes Devoured by his Horses",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "4",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/61/Gustave_Moreau_003.jpg/800px-Gustave_Moreau_003.jpg",
      title: "Hercules and the Hydra",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "5",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/67/Gustave_Moreau_-_Jacob_et_l%27ange.jpg/800px-Gustave_Moreau_-_Jacob_et_l%27ange.jpg",
      title: "Jacob and the Angel",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "6",
      url: "https://upload.wikimedia.org/wikipedia/commons/e/ef/Gustave_Moreau_-_Perseus_and_Andromeda%2C_1870.jpg",
      title: "Perseus and Andromeda",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "7",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Gustave_Moreau%2C_Samson_and_Delilah_%281882%29%2C_watercolor%2C_15.8_x.21.3_cm.%2C_Louvre.jpg/1280px-Gustave_Moreau%2C_Samson_and_Delilah_%281882%29%2C_watercolor%2C_15.8_x.21.3_cm.%2C_Louvre.jpg",
      title: "Samson and Delilah",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "8",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ed/The_Monkey_and_the_Dolphin.jpg/800px-The_Monkey_and_the_Dolphin.jpg",
      title: "The Monkey and the Dolphin",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
  ];

  if(props.data_art){
    for(let x in props.data_art){
      dummy_data.push(props.data_art[x]);
    }
  }

  //dummy_data.push(props.data_art);
  //console.log(dummy_data);
  console.log(props.data_art);
  console.log('1. dummy data');
  console.log(dummy_data);

  const art = {
    id: "0",
    url: "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5e/Gustave_Moreau_-_La_chim%C3%A8re.jpg/800px-Gustave_Moreau_-_La_chim%C3%A8re.jpg",
    title: "The Chimera",
    width: "737",
    height: "887",
    medium: "",
    artist: ""
  }

  const [isViewing, setIsViewing] = useState(false);
  const [currentSrc, setCurrentSrc] = useState('');
  const [currentTitle, setCurrentTitle] = useState('');
  //const [artData, setArtData] = useState(dummy_data);

  //console.log('2. artData is set');
  //console.log(artData);

  /* setArtData((prevState) => {
    console.log('inside setArtData');
    //return { ...prevState, artData};
  }) */
  
  //console.log('3. after setArtData');
  //console.log(artData);
 
  return (
    <div className={classes.body}>
        <br/>
      {dummy_data.map((art) => {
        return (
          <Card
            key={art.id}
            url={art.url}
            title={art.title}
            width={art.width}
            height={art.height}
            setIsViewing={setIsViewing}
            setCurrentSrc={setCurrentSrc}
            setCurrentTitle={setCurrentTitle}
          />
        );
      })}
      {isViewing && <Modal setIsViewing={setIsViewing} url={currentSrc} title={currentTitle}/>}
    </div>
  );
}

export default Body;
