import { useState, useEffect } from "react";
import { Route, Switch } from "react-router";

import Favorites from "./pages/Favorites";
import NewEntry from "./pages/NewEntry";

import "./App.css";
import MainNav from "./components/layout/MainNav";
import Main from './pages/Main';
import Body from "./components/layout/Body";

// fontawesome
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import { far } from "@fortawesome/free-regular-svg-icons";

// library creation for fontawesome
library.add(fas, far);

function App() {
  const [art, setArt] = useState(null);
  const [dataArt, setDataArt] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch("/api").then((res) => {
      res.json().then((data) => {
        setArt(data.message);
      });
    });
  });

  useEffect(() => {
    fetch("/art").then((res) => {
      res.json().then((data) => {
        // this doesn't work, need to update state
        //dummy_data.push(data.art);

        // right now data.art is one object, but when we have multiple objects,
        // I will need a helper function to distribute the data into a useable array
        setDataArt(data.art);
        console.log("dataArt in app", dataArt);
        setIsLoading(false);
      });
    });
  }, []);

  /* if(isLoading){
    return(
      <section>
        <p>Loading...</p>
      </section>
    )
  } */

  return (
    <div>
      <MainNav />
      <div>{!art ? <p>no art</p> : <p>{art}</p>}</div>
      <Switch>
        <Route path="/" exact>
          <Main data_art={dataArt} />
        </Route>
        <Route path="/favorites" exact>
          <Favorites />
        </Route>
        <Route path="/new" exact>
          <NewEntry />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
