import {useState} from 'react';
import classes from "./Card.module.css";

import Image from '../components/content/Image';

function Card(props) {

    const [isHovering, setIsHovering] = useState(false);
  function mouseEnterHandler() {
    setIsHovering(true);
    console.log('mouse enter');
  }
  function mouseLeaveHandler(){
      setIsHovering(false);
      console.log('mouse leave');
  }
  return (
    <div className={classes.card} onMouseEnter={mouseEnterHandler} onMouseLeave={mouseLeaveHandler}>
        {isHovering && <p>hello</p>}
      <Image
        url={props.url}
        title={props.title}
        width={props.width}
        height={props.height}
      />
    </div>
  );
}

export default Card;
