import {useState} from 'react';
import classes from "./Card.module.css";

import Image from '../components/content/Image';
import Actions from '../components/content/Actions';

function Card(props) {

    const [isHovering, setIsHovering] = useState(false);

    let art_src = '';

  function mouseEnterHandler() {
    setIsHovering(true);
  }

  function mouseLeaveHandler(){
      setIsHovering(false);
  }

  // Open the modal to view the art.
  function setIsViewing(e){
      props.setIsViewing(true);
      props.setCurrentSrc(e.target.currentSrc);
      props.setCurrentTitle(e.target.title);
  }
  
  return (
    <div className={classes.card} onMouseEnter={mouseEnterHandler} onMouseLeave={mouseLeaveHandler} onClick={setIsViewing}>
      <Image
        url={props.url}
        title={props.title}
        width={props.width}
        height={props.height}
        isHovering={isHovering}
      />
      {/*isHovering && <Actions />*/}
    </div>
  );
}

export default Card;
