// Main.js parent

import Card from "../../ui/Card";
import Modal from "../../ui/Modal";
import SwiperUI from "../../ui/SwiperUI";

import classes from "./Body.module.css";

import { useState, useEffect, Fragment } from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Body(props) {
  const dummy_data = [
    {
      id: "4",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/61/Gustave_Moreau_003.jpg/800px-Gustave_Moreau_003.jpg",
      title: "Hercules and the Hydra",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "5",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/67/Gustave_Moreau_-_Jacob_et_l%27ange.jpg/800px-Gustave_Moreau_-_Jacob_et_l%27ange.jpg",
      title: "Jacob and the Angel",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "6",
      url: "https://upload.wikimedia.org/wikipedia/commons/e/ef/Gustave_Moreau_-_Perseus_and_Andromeda%2C_1870.jpg",
      title: "Perseus and Andromeda",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "7",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Gustave_Moreau%2C_Samson_and_Delilah_%281882%29%2C_watercolor%2C_15.8_x.21.3_cm.%2C_Louvre.jpg/1280px-Gustave_Moreau%2C_Samson_and_Delilah_%281882%29%2C_watercolor%2C_15.8_x.21.3_cm.%2C_Louvre.jpg",
      title: "Samson and Delilah",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
    {
      id: "8",
      url: "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ed/The_Monkey_and_the_Dolphin.jpg/800px-The_Monkey_and_the_Dolphin.jpg",
      title: "The Monkey and the Dolphin",
      width: "758",
      height: "314",
      medium: "",
      artist: "",
    },
  ];

  const [isViewing, setIsViewing] = useState(false);
  const [currentSrc, setCurrentSrc] = useState("");
  const [currentTitle, setCurrentTitle] = useState("");

  if (props.data_art) {
    /* Sort art by artist */
    let sort_by_artist = [];

    for (const i in props.data_art) {
      if (!sort_by_artist[props.data_art[i].artist]) {
        sort_by_artist[props.data_art[i].artist] = [];
      }
      sort_by_artist[props.data_art[i].artist].push(props.data_art[i]);
    }
    /* End Sort art by artist */

    console.log(sort_by_artist);

    return (
      <Fragment>
        <br />
        <SwiperUI
          art={props.sort_by_artist}
          setIsViewing={setIsViewing}
          setCurrentSrc={setCurrentSrc}
          setCurrentTitle={setCurrentTitle}
        />
        {isViewing && ( // @TODO Make a portal for the modal
          <Modal
            setIsViewing={setIsViewing}
            url={currentSrc}
            title={currentTitle}
          />
        )}
      </Fragment>
    );
    /* return (
      <Fragment>
        <br />
        <SwiperUI
          art={props.data_art}
          setIsViewing={setIsViewing}
          setCurrentSrc={setCurrentSrc}
          setCurrentTitle={setCurrentTitle}
        />
        {isViewing && ( // @TODO Make a portal for the modal
          <Modal
            setIsViewing={setIsViewing}
            url={currentSrc}
            title={currentTitle}
          />
        )}
      </Fragment>
    ); */
  } else {
    return <div>There was an issue with retrieving the art!</div>;
  }
}

export default Body;
