/**
 * For now, we will just post all art data here. Eventually, we want to create
 * a new component called 'carosel' that we will put in here and that will be
 * the first level component.
 * App -> Main -> Body -> Carosel -> Card
 */

import { useState, useEffect } from "react";

import Body from "../components/layout/Body";

function Main() {
  const [dataArt, setDataArt] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch("/art").then((res) => {
      res.json().then((data) => {
        // this doesn't work, need to update state
        //dummy_data.push(data.art);

        // right now data.art is one object, but when we have multiple objects,
        // I will need a helper function to distribute the data into a useable array
        setDataArt(data.art);
        console.log(dataArt);
        setIsLoading(false);
      });
    });
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  return (
    <section>
      <Body data_art={dataArt} />
    </section>
  );
}

export default Main;
