import React, { useRef } from "react";

import { OutlinedInput, IconButton } from "@mui/material";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Search() {
  // add a button. put a ref on the input,
  // create a handler for the button that posts the query

  const searchRef = useRef();

  function searchHandler(e) {
    console.log("search ", searchRef.current.value);
    fetch("/searchart", {
      method: "POST",
      body: JSON.stringify(searchRef.current.value),
      headers: {
        "Content-Type": "application/json"
      },
    }).then((response) =>  {/* response.json()).then((json) => {
      console.log(json); */
    });
  }
  return (
    <div>
      <OutlinedInput placeholder="Search" inputRef={searchRef} /> {/* mui requires inputRef to pass a ref to the input element  */}
      <IconButton onClick={searchHandler}>
        <FontAwesomeIcon icon={["fas", "search"]} />
      </IconButton>
    </div>
  );
}

export default Search;
