// App.js parent

/**
 * For now, we will just post all art data here. Eventually, we want to create
 * a new component called 'carosel' that we will put in here and that will be
 * the first level component.
 * App -> Main -> Body -> Carosel -> Card
 */

import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import Body from "../components/layout/Body";

import Button from '@mui/material/Button';
import { OutlinedInput } from "@mui/material";

function Main() {
  const [dataArt, setDataArt] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch("/art").then((res) => {
      res.json().then((data) => {
        setDataArt(data.art);
        setIsLoading(false);
      });
    });
  }, [/* leave empty to get all records */]);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  return (
    <section>
      <Button variant="contained">Test</Button>
      <OutlinedInput placeholder="helo" />
      <Body data_art={dataArt} />
    </section>
  );
}

export default Main;
