import { createStore } from "redux";

const initialState = {
  art: [],
  favorites: [],
};

function sort_by_artist(art) {
  let sort_by_artist = [];

  for (const i in props.data_art) {
    if (!sort_by_artist[props.data_art[i].artist]) {
      sort_by_artist[props.data_art[i].artist] = [];
    }
    sort_by_artist[props.data_art[i].artist].push(props.data_art[i]);
  }
  return sort_by_artist;
}

function artReducer(state = initialState, action) {
  // This is the action type for showing all art (on page load)
  if (action.type === "allArt") {
    return {
      art: action.payload,
      favorites: state.favorites,
    };
  }

  return state;
}

const artStore = createStore(artReducer);

export default artStore;
