import { createStore } from "redux";

const initialState = {
  art: [],
  favorites: [],
};

function artReducer(state = initialState, action) {


    // This is the action type for showing all art (on page load)
  if (action.type === "allArt") {
    return {
      art: action.payload,
      favorites: state.favorites,
    };
  }

  return state;
}

const artStore = createStore(artReducer);

export default artStore;
